mapper.select:
 		SELECT COUNT(1) FROM `app_info`
		
			
				AND `softwareName` LIKE CONCAT ('%',#{querySoftwareName},'%') 
			
			
				AND `status` = #{queryStatus} 
			
			
				AND `flatformId` = #{queryFlatformId} 
			
			
				AND `categoryLevel1` = #{queryCategoryLevel1} 
			
			
				AND `categoryLevel2` = #{queryCategoryLevel2} 
			
			
				AND `categoryLevel3` = #{queryCategoryLevel3}

SELECT i.*, (SELECT d.valueName FROM `data_dictionary` d WHERE d.`valueId`=i.`status` AND d.`typeCode`= 'APP_STATUS') AS statusName, (SELECT d.valueName FROM `data_dictionary` d WHERE d.`valueId`=i.`status` AND d.`typeCode`= 'APP_FLATFORM') AS flatformName, (SELECT u.devName FROM `dev_user` u WHERE u.id = i.`devId`) AS devName, (SELECT v.versionNo FROM `app_version` v WHERE v.id = i.`versionId`) AS versionNo, (SELECT `categoryName` FROM `app_category` c WHERE c.id = i.`categoryLevel1`) AS categoryLevel1Name, (SELECT `categoryName` FROM `app_category` c WHERE c.id = i.`categoryLevel2`) AS categoryLevel2Name, (SELECT `categoryName` FROM `app_category` c WHERE c.id = i.`categoryLevel3`) AS categoryLevel3Name FROM `app_info` i
AND `softwareName` LIKE CONCAT ('%',#{querySoftwareName},'%')
AND `status` = #{queryStatus}
AND `flatformId` = #{queryFlatformId}
AND `categoryLevel1` = #{queryCategoryLevel1}
AND `categoryLevel2` = #{queryCategoryLevel2}
AND `categoryLevel3` = #{queryCategoryLevel3}
LIMIT #{from},#{pageSize}

mapper.update:
 		UPDATE `app_info`
		SET logoPicPath = NULL,logoLocPath = NULL
		WHERE id = #{id}
